import FilterButton from "./FilterButton";

function FilterContainer() {
  const srcList = [
    "/images/전체.svg",
    "/images/상품.svg",
    "/images/카테고리.svg",
    "/images/기획전.svg",
    "/images/브랜드.svg",
  ];

  return (
    <div>
      <ul className="Filter_container">

        <div className="Filter_container_A">
          {FilterButton(srcList[0])}

          <div className="Title_Text">    
              <p className="centered_text">전체</p>{" "}
              {/* centered_text 클래스를 추가합니다 */}           
          </div>
        </div>

        <FilterButton />
        <FilterButton />
        <FilterButton />
      </ul>
    </div>
  );
}

export default FilterContainer;

