import Product from "../components/Product";
import Category from "../components/Category";
import Exhibition from "../components/Exhibition";
import Brand from "../components/Brand";
import { useState, useEffect } from "react";

function ItemList() {
  const [productData, setProductData] = useState([]);

  // useEffect 훅을 사용하여 API 데이터 가져오기
  useEffect(() => {
    fetch("http://cozshopping.codestates-seb.link/api/v1/products?count=10")
      .then((response) => {
        if (!response.ok) {
          throw new Error(response.statusText);
        }
        return response.json();
      })
      .then((json) => {
        setProductData(json);
      })
      .catch((error) => console.error(error));
  }, []);

  // 첫 번째 데이터만 선택하여 렌더링
  const selectedData = filteredData.length > 0 ? [filteredData[0]] : [];

  const extractedData = selectedData.map((item) => ({
    img: item.image_url,
    name: item.brand_name,
    customers: item.customers,
    itemId: item.itemId,
  }));

  const selectedType = "Brand"; // 필터링할 타입 값

  // selectedType을 기준으로 필터링하여 해당하는 데이터 선택
  const filteredData = productData.filter((item) => item.type === selectedType);

  return (
    <div className="Item_container_Product">
      <ul className="Item_container_List_Product">
        <Product />

        <Category />

        <Exhibition />

        {extractedData.map((item, index) => (
          <div key={index}>
            <Brand
              img={item.image_url}
              name={item.bran_dname}
              customers={item.follower}
              itemId={item.id}
            />

          </div>
        ))}
      </ul>

      <ul className="Item_container_List_Product">
        <Brand />
        <Category />
        <Product />
        <Exhibition />
      </ul>

      <ul className="Item_container_List_Product">
        <Brand />
        <Category />
        <Product />
        <Exhibition />
      </ul>
    </div>
  );
}

export default ItemList;
