import { useState , useEffect} from "react";
import Bookmark from "./Bookmark";

function Brand({img,name,customers,itemId}) {

  const [productData, setproductData] = useState([]);

    //useEffect라는 hook 안에서 fetch를 부른다.
    useEffect(() => {
      fetch("http://cozshopping.codestates-seb.link/api/v1/products")
        .then((response) => {
          //네트워크가 제대로 동작 안 할 때
          if (!response.ok) {
            throw new Error(response.statusText);
          }
          return response.json();
        })
        .then((json) => {
          //상태값에 담는다던지
          setproductData(json)
        })
        .catch((error) => console.error(error));
        //데이터를 최초 렌더링 시 한 번만 받아오겠다
    }, []);


  return (
    <div className="Item_IMG_G">
      <div className="add_BookMark">
        <Bookmark/>
        <img className="Item_IMG" src={img} alt="Logo" />
      </div>

      <div className="Bottom_Text">
        <div className="Text_top">
          <p className="Item_name">{name}</p>
          <p className="Item_name2">{customers}</p>
        </div>
        <p className="Item_P">{itemId}</p>
      </div>
    </div>
  );
}

export default Brand;
