import FilterButton from "./FilterButton";
import React, { useState, useEffect } from "react";

function FilterContainer() {
  // let [srcList] = useState(["/images/전체.svg", "/images/브랜드.svg"]);

  const [srcList, setSrcList] = useState([]);


  useEffect(() => {
    const fetchData = async () => {
      try {
        const response = await fetch("http://cozshopping.codestates-seb.link/api/v1/products?count=4");
        const data = await response.json();
        setSrcList(data);  // API에서 가져온 데이터를 srcList에 설정합니다.
      } catch (error) {
        console.log(error);
      }
    };

    fetchData();
  }, []);

  return (
    <div>
      <ul className="Filter_container">      
        <div>
          {FilterButton(srcList.map((src, index)=>(<div key={index}/>)))}
          <p>타입 종류</p>
        </div>
        <div>
          {FilterButton(srcList[1])}
          <p>타입 종류</p>
        </div>

        <FilterButton />
        <FilterButton />
        <FilterButton />
      </ul>
    </div>
  );
}

export default FilterContainer;

