import Product from "../components/Product";
import Category from "../components/Category";
import Exhibition from "../components/Exhibition";
import Brand from "../components/Brand";
import { useState, useEffect } from "react";

function ItemList() {
  const [productData, setProductData] = useState([]);

  // useEffect 훅을 사용하여 API 데이터 가져오기
  useEffect(() => {
    fetch("http://cozshopping.codestates-seb.link/api/v1/products?count=10")
      .then((response) => {
        if (!response.ok) {
          throw new Error(response.statusText);
        }
        return response.json();
      })
      .then((json) => {
        setProductData(json);
      })
      .catch((error) => console.error(error));
  }, []);

  // 첫 번째 데이터만 선택하여 렌더링
  const selectedData = productData.length > 0 ? [productData[0]] : [];

  return (
    <div className="Item_container_Product">
      <ul className="Item_container_List_Product">
        {selectedData.map((item, index) => (
          <div key={index}>

            <Product />

            <Brand
              img={item.image_url}
              name={item.brand_name}
              customers={item.customers}
              itemId={item.itemId}
            />
          </div>
        ))}

        <Category />

        <Exhibition />
      </ul>

      <ul className="Item_container_List_Product">
        <Brand />
        <Category />
        <Product />
        <Exhibition />
      </ul>

      <ul className="Item_container_List_Product">
        <Brand />
        <Category />
        <Product />
        <Exhibition />
      </ul>
    </div>
  );
}

export default ItemList;
