import Product from "../components/Product";
import Category from "../components/Category";
import Exhibition from "../components/Exhibition";
import Brand from "../components/Brand";
import { useState , useEffect } from "react";


function ItemList(){
  const [productData, setproductData] = useState([]);

  //useEffect라는 hook 안에서 fetch를 부른다.
  useEffect(() => {
    fetch("http://cozshopping.codestates-seb.link/api/v1/products")
      .then((response) => {
        //네트워크가 제대로 동작 안 할 때
        if (!response.ok) {
          throw new Error(response.statusText);
        }
        return response.json();
      })
      .then((json) => {
        //상태값에 담는다던지
        setproductData(json)
      })
      .catch((error) => console.error(error));
      //데이터를 최초 렌더링 시 한 번만 받아오겠다
  }, []);


  return(
    <div className="Item_container_Product">
    <ul className="Item_container_List_Product">
    {productData.map((image_url, index) => (
          <div key={index}>
            <Brand img={image_url} str={productData[index]} /> 
            {/* img와 str에 값을 전달 */}
          </div>
        ))}

      <Brand />
      <Category />
      <Product />
      <Exhibition />
    </ul>

    <ul className="Item_container_List_Product">
      <Brand />
      <Category />
      <Product />
      <Exhibition />
    </ul>

    <ul className="Item_container_List_Product">
      <Brand />
      <Category />
      <Product />
      <Exhibition />
    </ul>
  </div>

  );
}

export default ItemList;
